import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./QuranApp.css";

import imgOne from '../../images/apptafseer.jpeg'
export default class WhatweDo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
 

  render() {
    return (
      <div className="container-fluid" style={{backgroundColor:'white'}}>
          <div style={{backgroundImage:`url(${imgOne})`}} className='back'>
        <div style={{backgroundColor:'rgba(0,0,0,.5)'}} className='p-5'>
          <h1 className='p-3 text-white'>Online Quran Pak Tafteesh</h1>
        </div>
      </div>
<h3 className='head pb-3'>Quran Application SOP</h3>
<p className='para'>Quran Application Many organisations and individuals have uploaded whole Quran on Play Store and Apple Store.
     Many download and recite the same. Errors and omissions have been noted. These errors and omissions are not detected 
     by ordinary persons. The recitation is intentionally not done correctly. For correct recitation we have started verifying 
     Quranic version available on Play Store and Apple Store. For this purpose we have emplyed HUFFAZ (WHO HAVE MEMORIZED WHOLE QURAN).
      One can see the list of Quran verified with status,
    whether with errors or without errors. We request you to please download only Quran verified and found without errors.</p>
</div>
    );
  }
}
